import React, {Component} from 'react';

class Calculator extends Component {
  constructor() {
    super();
    this.state = {
      displayNumber: '',
      runningTotal: 0,
      operator: '',
      operatorClicked: false,
    };
  }

  handleNumberButtonClick = digit => {
    let newDisplayNumber = 0;
    // update display number
    newDisplayNumber = this.state.displayNumber + digit;
    this.setState({displayNumber: newDisplayNumber});
  };

  handleOperation = () => {
    let newDisplayNumber = 0;
    if (this.state.operator === '/') {
      const newRunningTotal = this.state.runningTotal / newDisplayNumber;
      this.setState({runningTotal: newRunningTotal});
    } else if (this.state.operator === 'x') {
      const newRunningTotal = this.state.runningTotal * newDisplayNumber;
      this.setState({runningTotal: newRunningTotal});
    } else if (this.state.operator === '-') {
      const newRunningTotal = this.state.runningTotal - newDisplayNumber;
      this.setState({runningTotal: newRunningTotal});
    } else if (this.state.operator === '+') {
      const newRunningTotal = this.state.runningTotal + newDisplayNumber;
      this.setState({runningTotal: newRunningTotal});
    }
  };

  // first entering a number
  // enter an operator
  // enter a number
  // enter another operator or
  //    enter equals sign

  handleOperatorButtonClick = operatorInput => {
    this.setState({operator: operatorInput});
    console.log('operator', this.state.operator);
    this.setState({operatorClicked: true});
  };

  handleClearButtonClick = () => {
    this.setState({runningTotal: 0, displayNumber: 0});
  };

  render() {
    return (
      <div style={calcContainer}>
        <div style={screenContainer}>{this.state.displayNumber}</div>
        <div>
          <button
            style={buttonContainer}
            onClick={() => this.handleClearButtonClick()}
          >
            C
          </button>
        </div>
        <div style={rows}>
          <button
            style={buttonContainer}
            onClick={() => this.handleNumberButtonClick(7)}
          >
            7
          </button>
          <button
            style={buttonContainer}
            onClick={() => this.handleNumberButtonClick(8)}
          >
            8
          </button>
          <button
            style={buttonContainer}
            onClick={() => this.handleNumberButtonClick(9)}
          >
            9
          </button>
          <button
            style={buttonContainer}
            onClick={() => this.handleOperatorButtonClick('/')}
          >
            /
          </button>
        </div>
        <div style={rows}>
          <button
            style={buttonContainer}
            onClick={() => this.handleNumberButtonClick(4)}
          >
            4
          </button>
          <button
            style={buttonContainer}
            onClick={() => this.handleNumberButtonClick(4)}
          >
            5
          </button>
          <button
            style={buttonContainer}
            onClick={() => this.handleNumberButtonClick(4)}
          >
            6
          </button>
          <button style={buttonContainer}>x</button>
        </div>
        <div style={rows}>
          <button
            style={buttonContainer}
            onClick={() => this.handleNumberButtonClick(1)}
          >
            1
          </button>
          <button
            style={buttonContainer}
            onClick={() => this.handleNumberButtonClick(2)}
          >
            2
          </button>
          <button
            style={buttonContainer}
            onClick={() => this.handleNumberButtonClick(3)}
          >
            3
          </button>
          <button
            style={buttonContainer}
            onClick={() => this.handleOperatorButtonClick('-')}
          >
            -
          </button>
        </div>
        <div style={rows}>
          <button
            style={buttonContainer}
            onClick={() => this.handleNumberButtonClick(4)}
          >
            0
          </button>
          <button
            style={buttonContainer}
            onClick={() => this.handleDecimalButtonClick('.')}
          >
            .
          </button>
          <button
            style={buttonContainer}
            onClick={() => this.handleOperatorButtonClick('+')}
          >
            +
          </button>
          <button
            style={buttonContainer}
            onClick={() => this.handleOperatorButtonClick('=')}
          >
            =
          </button>
        </div>
      </div>
    );
  }
}

const calcContainer = {
  justifyContent: 'center',
};

const screenContainer = {
  margin: 'auto',
  border: '.5px solid green',
  padding: '25px',
  width: '200px',
};

const rows = {
  display: 'flex',
  justifyContent: 'center',
};

const buttonContainer = {
  border: '.5px solid green',
  padding: '25px',
};

export default Calculator;
