import React, {Component} from 'react';

class Calculator extends Component {
  constructor() {
    super();
    this.state = {
      displayNumber: '',
      runningTotal: 0,
      operator: '',
      operatorClicked: false,
    };
  }

  handleNumberButtonClick = digit => {
    // before operator button click
    let newDisplayNumber = 0;
    if (!this.state.operatorClicked) {
      newDisplayNumber = this.state.displayNumber + digit;
      this.setState({displayNumber: newDisplayNumber});
    } else {
      if (this.state.operator === '/') {
        const newRunningTotal = this.state.runningTotal / newDisplayNumber;
        this.setState({runningTotal: newRunningTotal});
      } else if (this.state.operator === '/') {
        const newRunningTotal = this.state.runningTotal / newDisplayNumber;
        this.setState({runningTotal: newRunningTotal});
      } else if (this.state.operator === '/') {
        const newRunningTotal = this.state.runningTotal / newDisplayNumber;
        this.setState({runningTotal: newRunningTotal});
      }
    }
    // after operator button click
  };

  handleOperatorButtonClick = operatorInput => {
    this.setState({operator: operatorInput});
    console.log('operator', this.state.operator);
    this.setState({operatorClicked: true});
  };

  handleSubmit = () => {};

  render() {
    return (
      <div style={calcContainer}>
        <div style={screenContainer}>{this.state.displayNumber}</div>
        <div style={rows}>
          <button
            style={buttonContainer}
            onClick={() => this.handleNumberButtonClick(7)}
          >
            7
          </button>
          <button
            style={buttonContainer}
            onClick={() => this.handleNumberButtonClick(8)}
          >
            8
          </button>
          <button
            style={buttonContainer}
            onClick={() => this.handleNumberButtonClick(9)}
          >
            9
          </button>
          <button
            style={buttonContainer}
            onClick={() => this.handleOperatorButtonClick('/')}
          >
            /
          </button>
        </div>
        <div style={rows}>
          <button style={buttonContainer}>4</button>
          <button style={buttonContainer}>5</button>
          <button style={buttonContainer}>6</button>
          <button style={buttonContainer}>x</button>
        </div>
        <div style={rows}>
          <button style={buttonContainer}>1</button>
          <button style={buttonContainer}>2</button>
          <button style={buttonContainer}>3</button>
          <button style={buttonContainer}>-</button>
        </div>
        <div style={rows}>
          <button style={buttonContainer}>0</button>
          <button style={buttonContainer}>.</button>
          <button style={buttonContainer}>+</button>
          <button style={buttonContainer}>=</button>
        </div>
      </div>
    );
  }
}

const calcContainer = {
  justifyContent: 'center',
};

const screenContainer = {
  margin: 'auto',
  border: '.5px solid green',
  padding: '25px',
  width: '200px',
};

const rows = {
  display: 'flex',
  justifyContent: 'center',
};

const buttonContainer = {
  border: '.5px solid green',
  padding: '25px',
};

export default Calculator;
